$:/Tekan/AddList.text:
\define boardandlist()
$(currentBoard)$/$(listname)$
\end

\define boardnametag()
[[$(currentBoard)$]]
\end

\define AddList()
<$action-createtiddler $basetitle=<<boardandlist>> tags=<<boardnametag>> caption=<<listname>>/>
<$action-listops $tiddler=<<currentBoard>> $subfilter="[<boardandlist>]"/>
<$action-setfield $tiddler="$:/state/newlisttemp" text=""/>
<$action-setfield $tiddler="$:/state/addnewlist" text=""/>
\end

\define CancelList()
<$action-setfield $tiddler="$:/state/newlisttemp" text=""/>
<$action-setfield $tiddler="$:/state/addnewlist" text=""/>
\end

<style>
.list-wrapper-add {
	height: auto;
	min-height: 30px;
	padding: 4px;
	background-color: #E2E4E6;
	-webkit-transition: background 85ms ease-in,opacity 40ms ease-in,border-color 85ms ease-in;
	transition: background 85ms ease-in,opacity 40ms ease-in,border-color 85ms ease-in;
}
.list-name-input {
	background: rgba(0,0,0,.05) !important;
	box-shadow: inset 0 1px 8px rgba(0,0,0,.15);
    border: 1px solid #aaa;
	display: block;
	margin: 0;
	-webkit-transition: margin 85ms ease-in,background 85ms ease-in;
	transition: margin 85ms ease-in,background 85ms ease-in;
	width: 100%;
	margin-bottom: 10px;
	padding: 7px;
}
</style>

<$reveal state="$:/state/addnewlist" type="nomatch" text="list">
 <div class="list">
		<div class="semitransparent"> 
        <$button class="semitransparentborderless" style="width:100%;text-align:left;" set="$:/state/addnewlist" setTo="list">     
        Add a list...
        </$button>
		</div>
</div>
</$reveal>

<$reveal state="$:/state/addnewlist" type="match" text="list">
<div class="list list-wrapper-add">


	<$edit-text default="New list" class="list-name-input" tiddler="$:/state/newlisttemp" placeholder="Add a list…" tag="input"/>
    <$set name="listname" value={{$:/state/newlisttemp}}>
	<$button class="smallgreenbutton" actions=<<AddList>>>Add list</$button>
    <$button class="tc-btn-invisible" style="padding: 5px;fill: #999;" actions=<<CancelList>>>{{$:/core/images/close-button}}</$button>
    </$set>


</div>
</$reveal>

$:/Tekan/Stepping-away-from-TW5-styles.text:
.tc-dropzone p {
    margin: 0
}

label {
    display: block;
    font-weight: 700;
    margin-bottom: 4px;
    color: #333;
}


/* Stepping away from TW5 styles*/

.tc-notification {
    display: none
}

.tc-droppable.tc-dragover>.tc-droppable-placeholder {
    height: 1px !important;
    border: none !important;
    border-bottom: 2px solid grey !important;
}

.tc-modal {
    background-color: #EDEFF0 !important;
    border-radius: 2px;
    margin: 3pc 0 5pc;
    overflow: hidden;
    position: relative;
    width: 60% !important;
    left: 20% !important;
}

.tc-modal-header {
    display: none !important;
}

.tc-modal-footer {
    display: none !important;
}

$:/Tekan/Substyles.text:
.ui {
	font: 12px Arial,Helvetica,sans-serif;
margin: 0;
	line-height: 1.3em;
}

.ui {
	font-size: 12px !important;

}
.ui {
  height: 100vh;
  display: -ms-grid;
  display: grid;
  -ms-grid-rows: 40px 50px 1fr;
      grid-template-rows: 40px 50px 1fr;
  background-color: #0079bf;
  color: #eee;
}

.navbar {
  padding-left: 10px;
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-align: center;
      -ms-flex-align: center;
          align-items: center;
}
.navbar.app {
  background-color: #0067a3;
  font-size: 1.5rem;
}
.navbar.board {
	font: 18px Arial,Helvetica,sans-serif;
	cursor: default;
	font-weight: 700;
	line-height: 30px;
}

.lists {
	display: -webkit-box;
	display: -ms-flexbox;
	display: flex;
	overflow-x: auto;

}
.lists > * {
  -webkit-box-flex: 0;
      -ms-flex: 0 0 auto;
          flex: 0 0 auto;
  margin-left: 10px;
}
.lists::after {
  content: '';
  -webkit-box-flex: 0;
      -ms-flex: 0 0 10px;
          flex: 0 0 10px;
}

.list {
  width: 270px;
  height: calc(100% - 10px - 17px);
}
.list > * {
  background-color: #e2e4e6;
  color: #333;
  padding: 0 8px;
}
.list header {
  line-height: 36px;
  font-size: 16px;
  font-weight: bold;
  border-top-left-radius: 5px;
  border-top-right-radius: 5px;
  position: relative;
}
.list footer {
  line-height: 36px;
  color: #888;
}
.list .footer {
   border-bottom-left-radius: 5px;
  border-bottom-right-radius: 5px;
}
.list ul {
  list-style: none;
  margin: 0;
  max-height: calc(100% - 36px - 36px);
  overflow-y: auto;
}
.list ul li {
	background-color: #fff;
	padding: 10px;
	border-radius: 3px;
	box-shadow: 0 1px 1px rgba(0, 0, 0, 0.1);
	margin-bottom: 10px;
}

.list ul li img {
  display: block;
  width: calc(100% + 2 * 10px);
  margin: -10px 0 10px -10px;
  border-top-left-radius: 3px;
  border-top-right-radius: 3px;
}

/* Substyles */

.compact-board-tile-wrapper {
	color: #333;
	font-size: 15px;
	line-height: 36px;
	background-color: #E0EFF7;
	margin-bottom: 5px;
	border-radius: 3px;
     overflow: hidden;
       text-overflow: ellipsis;
}

footer .tc-reveal { padding: 10px 0 4px 0; }


footer textarea {
	background: #fff;
	border-radius: 5px;
	border: none;
	box-shadow: none;
	height: auto;
	margin-bottom: 4px;
	margin-top: 10px;
	max-height: 162px;
	min-height: 54px;
	overflow-y: auto;
	padding: 0;
	padding: 8px;
	width: 100%; }


.header-btn-icon button {
	background: linear-gradient(to bottom,rgba(255,255,255,.3) 0,rgba(255,255,255,.2) 100%);
	border-radius: 3px;
	border: 0;
	color: #f3f3f3;
	display: block;
	float: left;
	font-weight: 700;
	font-family: Arial,Helvetica,sans-serif;
    font-size: 14px;
	line-height: 30px;
	margin-right: 8px;
	min-width: 30px;
	padding: 0 8px;
	text-decoration: none; }


.header-user {
	position: absolute;
	right: 0; }



/* Task List */

.list-card-operation {
	background-clip: padding-box;
	background-color: #EDEFF0;
	background-origin: padding-box;
	opacity: 0;
	 }


.list ul li.tc-draggable {
  position: relative;  }


.list ul li.tc-draggable:hover {
	background-color: #EDEFF0;
	border-bottom-color: #D6DADC;  }

	.list ul li.tc-draggable:hover .list-card-operation { opacity: 1; }


.icon-sm {
	color: #400;
	display: inline-block;
	font-family: trellicons;
	font-size: 12pt;
	font-style: normal;
	font-weight: 400;
	height: 30px;
	line-height: 25px;
	text-align: center;
	text-decoration: none;
	width: 30px; }

.listitem {
  	position: absolute;
	right: 3px;
	top: 3px;
  	z-index: 40; 
	border-radius: 3px;
	padding: 4px;	}
	.listitem:hover { background-color: #D6DADC; }




 /* Modal Button */
.materialbutton {
	background-color: #26a69a;
